import React, { useState, useCallback } from 'react';
import ReactMarkdown from 'react-markdown';
import './PhotoAnalysis.css';

// WARNING: DO NOT EXPOSE YOUR API KEY PUBLICLY.
// This is for local testing ONLY. Remove or secure via a backend for production.
const GEMINI_API_KEY = 'AIzaSyDkx4L2FvHs8Xqd_YSuhVCPhU0rx37PHZM';
const GEMINI_API_URL = `https://generativelanguage.googleapis.com/v1beta/models/gemini-1.5-flash-latest:generateContent?key=${GEMINI_API_KEY}`;

// Helper function to convert file to base64
const fileToGenerativePart = async (file) => {
  const base64EncodedDataPromise = new Promise((resolve) => {
    const reader = new FileReader();
    reader.onloadend = () => resolve(reader.result.split(',')[1]);
    reader.readAsDataURL(file);
  });
  return {
    inlineData: { data: await base64EncodedDataPromise, mimeType: file.type },
  };
};

function PhotoAnalysis() {
  const [selectedFile, setSelectedFile] = useState(null);
  const [previewUrl, setPreviewUrl] = useState(null);
  const [analysisResult, setAnalysisResult] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);

  const handleFileChange = useCallback((event) => {
    const file = event.target.files[0];
    if (file) {
      setSelectedFile(file);
      setPreviewUrl(URL.createObjectURL(file));
      setAnalysisResult(null);
      setError(null);
    } else {
      setSelectedFile(null);
      setPreviewUrl(null);
    }
  }, []);

  const handleAnalysis = useCallback(async () => {
    if (!selectedFile) return;

    setIsLoading(true);
    setAnalysisResult(null);
    setError(null);

    try {
      const imagePart = await fileToGenerativePart(selectedFile);
      const prompt = "Ïù¥ ÏÇ¨ÏßÑÏùÑ ÏûêÏÑ∏Ìûà ÏÑ§Î™ÖÌï¥Ï£ºÏÑ∏Ïöî. ÏÇ¨ÏßÑ ÏÜçÏóê Î¨¥ÏóáÏù¥ ÏûàÎäîÏßÄ, Ïñ¥Îñ§ Î∂ÑÏúÑÍ∏∞Ïù∏ÏßÄ, Ïñ¥Îñ§ Í∞êÏ†ïÏù¥ ÎäêÍª¥ÏßÄÎäîÏßÄ ÏïåÎ†§Ï£ºÏÑ∏Ïöî. ÎßàÌÅ¨Îã§Ïö¥ ÌòïÏãù(Ïòà: **Ï§ëÏöîÌïú Î∂ÄÎ∂Ñ**)ÏùÑ ÏÇ¨Ïö©ÌïòÏó¨ Í∞ïÏ°∞Ìï† Î∂ÄÎ∂ÑÏùÑ ÌëúÏãúÌï¥Ï£ºÏÑ∏Ïöî. ÎòêÌïú, Ïù¥ ÏÇ¨ÏßÑÍ≥º Í¥ÄÎ†®ÌïòÏó¨ ÏÇ¨Ïö©ÏûêÏôÄ ÏÉÅÌò∏ÏûëÏö©Ìï† Ïàò ÏûàÎäî Ï∞ΩÏùòÏ†ÅÏù∏ ÌôúÎèôÏù¥ÎÇò ÏßàÎ¨∏ 3Í∞ÄÏßÄÎ•º Ï†úÏïàÌï¥Ï£ºÏÑ∏Ïöî.";

      const requestBody = {
        contents: [
          {
            parts: [
              { text: prompt },
              imagePart,
            ],
          },
        ],
        // Optional: Add generationConfig if needed
        // generationConfig: {
        //   maxOutputTokens: 2048,
        //   temperature: 0.4,
        //   topP: 1,
        //   topK: 32
        // }
      };

      const response = await fetch(GEMINI_API_URL, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(requestBody),
      });

      if (!response.ok) {
        const errorData = await response.json();
        console.error('Gemini API Error:', errorData);
        throw new Error(`API ÏöîÏ≤≠ Ïã§Ìå®: ${response.status} ${response.statusText}. ${errorData?.error?.message || ''}`);
      }

      const responseData = await response.json();

      if (responseData.candidates && responseData.candidates.length > 0 && responseData.candidates[0].content) {
        const parts = responseData.candidates[0].content.parts;
        let fullTextResponse = "";
        parts.forEach(part => {
          if (part.text) {
            fullTextResponse += part.text + '\n';
          }
        });

        setAnalysisResult({
          fileName: selectedFile.name,
          apiResponseText: fullTextResponse.trim(),
          // You might want to parse more structured data if the API provides it,
          // or use further prompts to get structured JSON.
          // For now, we display the raw text response.
        });
      } else if (responseData.promptFeedback && responseData.promptFeedback.blockReason) {
        setError(`Î∂ÑÏÑùÏù¥ Ï∞®Îã®ÎêòÏóàÏäµÎãàÎã§: ${responseData.promptFeedback.blockReason} - ${responseData.promptFeedback.safetyRatings?.map(r => `${r.category} (${r.probability})`).join(', ') || 'ÏÑ∏Î∂Ä Ï†ïÎ≥¥ ÏóÜÏùå'}`);
      } else {
        console.warn("Unexpected API response structure:", responseData);
        setError('AI Î∂ÑÏÑù Í≤∞Í≥ºÎ•º Í∞ÄÏ†∏ÏôîÏúºÎÇò, ÏòàÏÉÅÏπò Î™ªÌïú ÏùëÎãµ Íµ¨Ï°∞ÏûÖÎãàÎã§.');
      }

    } catch (err) {
      console.error("Error during API call:", err);
      setError(`Î∂ÑÏÑù Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§: ${err.message}`);
    } finally {
      setIsLoading(false);
    }
  }, [selectedFile]);

  return (
    <section className="photo-analysis-section">
      <h2>üì∏ ÏÇ¨ÏßÑ Î∂ÑÏÑù Î∞è ÏÉÅÌò∏ÏûëÏö©</h2>

      <div className="upload-area">
        <input type="file" accept="image/*" onChange={handleFileChange} />
        {previewUrl && (
          <div className="image-preview">
            <img src={previewUrl} alt="ÏóÖÎ°úÎìúÎêú ÏÇ¨ÏßÑ ÎØ∏Î¶¨Î≥¥Í∏∞" />
          </div>
        )}
      </div>

      <button 
        className="analysis-button" 
        onClick={handleAnalysis} 
        disabled={!selectedFile || isLoading}
      >
        {isLoading ? 'Gemini AI Î∂ÑÏÑù Ï§ë...' : 'AI Î∂ÑÏÑù ÏãúÏûë'}
      </button>

      {isLoading && <p className="loading-message">Gemini AIÍ∞Ä ÏÇ¨ÏßÑÏùÑ Î∂ÑÏÑùÌïòÍ≥† ÏûàÏäµÎãàÎã§. Ïû†ÏãúÎßå Í∏∞Îã§Î†§ Ï£ºÏÑ∏Ïöî...</p>}
      {error && <p className="error-message" style={{color: 'red', textAlign: 'center', whiteSpace: 'pre-wrap'}}>{error}</p>}

      {analysisResult && (
        <div className="analysis-result">
          <h3>AI Î∂ÑÏÑù Í≤∞Í≥º</h3>
          <p><strong>ÌååÏùºÎ™Ö:</strong> {analysisResult.fileName}</p>
          <h4>AIÏùò Î∂ÑÏÑù ÎÇ¥Ïö©:</h4>
          <div className="markdown-content">
            <ReactMarkdown>{analysisResult.apiResponseText}</ReactMarkdown>
          </div>
        </div>
      )}
    </section>
  );
}

export default PhotoAnalysis;
