import React, { useState, useCallback } from 'react';
import './PhotoAnalysis.css';

function PhotoAnalysis() {
  const [selectedFile, setSelectedFile] = useState(null);
  const [previewUrl, setPreviewUrl] = useState(null);
  const [analysisResult, setAnalysisResult] = useState(null);
  const [isLoading, setIsLoading] = useState(false);

  const handleFileChange = useCallback((event) => {
    const file = event.target.files[0];
    if (file) {
      setSelectedFile(file);
      setPreviewUrl(URL.createObjectURL(file));
      setAnalysisResult(null); // Reset previous analysis
    } else {
      setSelectedFile(null);
      setPreviewUrl(null);
    }
  }, []);

  const handleAnalysis = useCallback(() => {
    if (!selectedFile) return;

    setIsLoading(true);
    setAnalysisResult(null);

    // Simulate AI analysis
    setTimeout(() => {
      const mockAnalysis = {
        fileName: selectedFile.name,
        objects: ['나무', '하늘', '사람', '미소'],
        emotions: ['행복', '평화로움'],
        context: '공원에서 즐거운 시간을 보내는 모습',
        ocrText: null, // 만약 이미지에 텍스트가 있다면 여기에 추가
        suggestions: [
          '이 사진 속 장소에 대한 기억을 떠올려 이야기 만들어보기',
          '사진 속 인물에게 짧은 편지 써보기',
          '비슷한 분위기의 다른 사진 찾아보기'
        ]
      };
      setAnalysisResult(mockAnalysis);
      setIsLoading(false);
    }, 2000);
  }, [selectedFile]);

  return (
    <section className="photo-analysis-section">
      <h2>📸 사진 분석 및 상호작용</h2>
      <p>사진을 업로드하면 AI가 사진의 요소들을 분석하고, 이를 바탕으로 다양한 활동을 제안합니다.</p>

      <div className="upload-area">
        <input type="file" accept="image/*" onChange={handleFileChange} />
        {previewUrl && (
          <div className="image-preview">
            <img src={previewUrl} alt="업로드된 사진 미리보기" />
          </div>
        )}
      </div>

      <button 
        className="analysis-button" 
        onClick={handleAnalysis} 
        disabled={!selectedFile || isLoading}
      >
        {isLoading ? '분석 중...' : 'AI 분석 시작'}
      </button>

      {isLoading && <p className="loading-message">AI가 사진을 분석하고 있습니다. 잠시만 기다려 주세요...</p>}

      {analysisResult && (
        <div className="analysis-result">
          <h3>AI 분석 결과</h3>
          <p><strong>파일명:</strong> {analysisResult.fileName}</p>
          <p><strong>주요 객체:</strong> {analysisResult.objects.join(', ')}</p>
          <p><strong>추정 감정:</strong> {analysisResult.emotions.join(', ')}</p>
          <p><strong>상황 설명:</strong> {analysisResult.context}</p>
          {analysisResult.ocrText && <p><strong>인식된 텍스트:</strong> {analysisResult.ocrText}</p>}
          <h4>다음 활동을 추천해요:</h4>
          <ul>
            {analysisResult.suggestions.map((suggestion, index) => (
              <li key={index}>{suggestion}</li>
            ))}
          </ul>
        </div>
      )}
    </section>
  );
}

export default PhotoAnalysis;
